const pathTool=require('path');
const Locator=module.exports= {
    instance: {},
    getInstance(name){
        return new this.instance[name];
    },
    register(name, clazz){
        this.instance[name] = clazz;
    }
};
Locator.register('local',class LocalLocator {
    constructor() {
    }
    locate(location){
        let path=pathTool.join(pathTool.dirname(require.main.filename),location);
        return Promise.resolve({path});
    }
})
Locator.register('npm', require('./NPMLocator'));